
public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x, y,z;
		
		x=4;
		y=5;
		z=6;
		
		++x;
		
		System.out.println(x);
		System.out.println(++y);	// first increments then use
		System.out.println(z++); 	//first use then increments
		
		// x= x+4; 
		x +=6;  // shorthand operator can be used with all mathematical operator
		
		System.out.println(x);
				
				
	}

}
